rotateEvent = () => {
  const region = new ZingTouch.Region(document.getElementById("controls"));
  const wheel = document.getElementById("wheel");

  let lastRotationTime = 0;

  region.bind(wheel, "rotate", (e) => {
    const now = Date.now();
    if (now - lastRotationTime < 250) return;
    lastRotationTime = now;

    let menuList = null;
    const { display } = this.state;

    if (!display) return;

    if (display.sideMenu) {
      menuList = document.querySelectorAll("#side-menu tr");
    } else if (display.music) {
      menuList = document.querySelectorAll("#music-menu tr");
    }

    if (!menuList || menuList.length <= 1) return;

    const menuArray = Array.from(menuList);

    // Only loop from index 1 (to skip title like iPod or Music)
    const items = menuArray.slice(1);
    let currentIndex = items.findIndex((item) => item.classList.contains("active"));

    // If none active, default to first real item
    if (currentIndex === -1) {
      items[0].classList.add("active");
      return;
    }

    // Remove active from current
    items[currentIndex].classList.remove("active");

    // Calculate next index
    let newIndex;
    if (e.detail.distanceFromLast > 0) {
      newIndex = (currentIndex + 1) % items.length;
    } else {
      newIndex = (currentIndex - 1 + items.length) % items.length;
    }

    // Set new active
    items[newIndex].classList.add("active");
  });
};


componentDidMount() {
  this.rotateEvent(); // This ensures ZingTouch binds after the DOM is ready
}

  // If ok button is clicked,  open the selected component

   handleOk = () => {
    const activeMenu = document.querySelector('#side-menu .active');
    const activeItemInMusic = document.querySelector('#music-menu .active');

    // If the active item is of SideMenu component
    if(activeMenu){
      const optionSelected = activeMenu.getAttribute('data-option');

      const display = this.state.display;
      // Mark all the display as false
      for(let key in display){  // Note here 'in' and not 'of'
        display[key] = false;
      }

      // Then mark true to the option selected
      display[optionSelected] = true;

      // Set state to render the components to open the relevant component
      this.setState({
        display: display,
        activeItemInMenu: optionSelected
      });
    }
    // If the active item is of MusicMenu component
    else if(activeItemInMusic){
      const optionSelected = activeItemInMusic.getAttribute('data-option');
      this.setState({
        toShowInMusicComponent: optionSelected,
        activeItemInMusic: optionSelected
      });
    }
  }

  // If menu button is clicked, go back to the menu screen

    // If menu button is clicked, go back
  handleMenuClick = () => {

    // If component of SideMenu was opened, then go back to SideMenu
    if(this.state.toShowInMusicComponent === 'musicMenu'){
      const display = this.state.display;
      // Mark all the display as false
      for(let key in display){  // Note here 'in' and not 'of'
        display[key] = false;
      }

      // Then mark true to the option selected
      display.sideMenu = true;

      this.setState({ 
        display: display,
        activeItemInMusic: 'allSongs'
      });
    }
    // If component of MusicMene was opened, then go back to MusicMenu
    else{ // Means toShowInMusicComponent != 'musicMenu'. Set it 'musicMenu'
      const optionSelected = this.state.toShowInMusicComponent;
      this.setState({ 
        toShowInMusicComponent: 'musicMenu',
        activeItemInMusic: optionSelected
      });
    }

  }

  render(){
     const { display, activeItemInMenu, toShowInMusicComponent, activeItemInMusic } 
     = this.state;
    return (
      <div id="iPod-app">
        <Screen
          display={display}
          activeItemInMenu={activeItemInMenu}
          toShowInMusicComponent={toShowInMusicComponent}
          activeItemInMusic={activeItemInMusic}
          />
        <Controls 
          handleOk={this.handleOk}
          handleMenuClick={this.handleMenuClick}
          rotateEvent={this.rotateEvent}

          />
      </div>
    );
  }

}

export default App;
