import React from 'react';
import './App.css';
import Screen from './components/Screen';

class App extends React.Component{

  constructor(){
    super();
    this.state={
      display:{
        'sideMenu':true, coverflow:false, 'music':false, 'games':false, 'settings':false
      },
      activeItemInMenu:'coverflow',
      activeItemInMusic:''
    }
  }

  // Handle the rotate event fired from the control component

  // If ok button is clicked,  open the selected component

  // If menu button is clicked, go back to the menu screen

  render(){
    return (
      <>
        <Screen/>
      </>
    );
  }

}

export default App;
