import React from 'react';
import './App.css';
import Coverflow from './components/Coverflow';
import Settings from './components/Settings';
import Music from './components/Music';
import Games from './components/Games';

class App extends React.Component{
  constructor(props){
     super(props);
     this.menuItems=['Cover Flow', 'Music', 'Games', 'Settings'];
     this.state={
      selectedIndex:0,
      activeScreen:'menu'
     }
  }
  

  // Handle the rotate event fired from the control component

  // If ok button is clicked,  open the selected component

  // If menu button is clicked, go back to the menu screen

  render(){
    return (
      <>
        <Coverflow/>
        <Music/>
        <Games/>
        <Settings/>
      </>
    );
  }

}

export default App;
